const content=document.querySelector("#bio-pg main section[role='content']");function getWidth(){const t=Array.from(document.getElementsByClassName("tab"));let e=0;for(let n=0;n<t.length;n++){e+=t[n].offsetWidth}return e}let hash=window.location.hash;const tabLinks=Array.from(document.getElementsByClassName("tab-link"));tabLinks.forEach((t=>{t.addEventListener("click",(()=>{hash=window.location.hash}))})),window.addEventListener("resize",(()=>{let t;tabLinks.forEach((e=>{e.getAttribute("href")==hash&&(t=tabLinks.indexOf(e))})),content.scrollLeft=getWidth()*(t/tabLinks.length)}));