const burger=document.getElementById("burger"),navClose=document.getElementById("nav-close"),nav=document.getElementById("nav"),navLinks=document.getElementsByClassName("navlink");burger.addEventListener("click",(()=>{nav.classList.add("active"),burger.classList.add("active");for(let e=0;e<navLinks.length;e++){const n=navLinks[e];n.style.animation?n.style.animation="":n.style.animation=`navLinkFade 0.5s ease forwards ${e/12+.3}s`}})),navClose.addEventListener("click",(()=>{nav.classList.remove("active"),burger.classList.remove("active");for(let e=0;e<navLinks.length;e++){navLinks[e].style.animation=""}}));