const tempIcons=Array.from(document.getElementsByClassName("temp-icon")),hideIfAtEnd=e=>{const t=e.parentElement;t.offsetTop==e.parentElement.nextElementSibling.offsetTop?(e.classList.remove("hide"),t.classList.remove("icon-hidden")):(e.classList.add("hide"),t.classList.add("icon-hidden"))};window.addEventListener("resize",(()=>{tempIcons.forEach((e=>hideIfAtEnd(e)))})),window.addEventListener("load",(()=>{for(let e=0;e<2;e++)tempIcons.forEach((e=>hideIfAtEnd(e)))}));